mapper.select:
SELECT count(*) FROM t_agent_account a1 LEFT JOIN t_agent_account a2 ON a1.parent_agent_code = a2.agent_code LEFT JOIN sys_user s on a1.sys_user_id = s.user_id LEFT JOIN t_withdrawal_record r on a1.agent_code = r.agent_code WHERE a1.sys_user_id != 1
AND a2.agent_name like concat('%', #{parentAgentName}, '%')
AND a1.agent_name like concat('%', #{agentName}, '%')
AND a1.is_real_name = #{isRealName}
AND a1.is_enabled = #{isEnabled}
AND a1.level = #{level}
ORDER BY a1.create_time desc

SELECT a1.agent_account_id as agentAccountId, s.user_id as sysUserId, s.user_name as sysUserName, a1.parent_agent_code as parentAgentCode, a2.agent_name as parentAgentName, a1.agent_code as agentCode, a1.agent_name as agentName, a1.level as level, a1.is_real_name as isRealName, a1.phone as phone, a1.is_enabled as isEnabled, a1.is_encrypt as isEncrypt, a1.create_time as createTime, r.balance as balance FROM t_agent_account a1 LEFT JOIN t_agent_account a2 ON a1.parent_agent_code = a2.agent_code LEFT JOIN sys_user s on a1.sys_user_id = s.user_id LEFT JOIN t_withdrawal_record r on a1.agent_code = r.agent_code WHERE a1.sys_user_id != 1
AND a2.agent_name like concat('%', #{parentAgentName}, '%')
AND a1.agent_name like concat('%', #{agentName}, '%')
AND a1.is_real_name = #{isRealName}
AND a1.is_enabled = #{isEnabled}
AND a1.level = #{level}
ORDER BY a1.create_time desc limit #{offset},#{pageSize}

SELECT COUNT(*)
FROM t_agent_account a1
WHERE a1.parent_agent_code = #{queryBO.parentAgentCode}
  AND a1.sys_user_id != 1

    AND (
        CAST(a1.agent_account_id AS CHAR) LIKE CONCAT('%', #{queryBO.searchKeyword}, '%')
        OR a1.agent_code LIKE CONCAT('%', #{queryBO.searchKeyword}, '%')
        OR a1.agent_name LIKE CONCAT('%', #{queryBO.searchKeyword}, '%')
        OR a1.phone LIKE CONCAT('%', #{queryBO.searchKeyword}, '%')
    )


    AND a1.is_real_name = #{queryBO.isRealName}


    AND a1.is_enabled = #{queryBO.isEnabled}


    AND a1.level = #{queryBO.level}

SELECT a1.agent_account_id as agentAccountId, a1.sys_user_id as sysUserId, s.user_name as sysUserName, a1.parent_agent_code as parentAgentCode, a2.agent_name as parentAgentName, a1.agent_code as agentCode, a1.agent_name as agentName, a1.level as level, a1.is_real_name as isRealName, a1.phone as phone, a1.is_enabled as isEnabled, a1.is_encrypt as isEncrypt, a1.create_time as createTime, r.balance as balance FROM t_agent_account a1 LEFT JOIN t_agent_account a2 ON a1.parent_agent_code = a2.agent_code LEFT JOIN sys_user s ON a1.sys_user_id = s.user_id LEFT JOIN t_withdrawal_record r ON a1.agent_code = r.agent_code WHERE a1.parent_agent_code = #{queryBO.parentAgentCode} AND a1.sys_user_id != 1
AND ( CAST(a1.agent_account_id AS CHAR) LIKE CONCAT('%', #{queryBO.searchKeyword}, '%') OR a1.agent_code LIKE CONCAT('%', #{queryBO.searchKeyword}, '%') OR a1.agent_name LIKE CONCAT('%', #{queryBO.searchKeyword}, '%') OR a1.phone LIKE CONCAT('%', #{queryBO.searchKeyword}, '%') )
AND a1.is_real_name = #{queryBO.isRealName}
AND a1.is_enabled = #{queryBO.isEnabled}
AND a1.level = #{queryBO.level}
ORDER BY a1.create_time DESC LIMIT #{offset}, #{pageSize}
